import Foundation
import MagazineLayout
import RxFlow
import RxSwift
import RxCocoa

struct RowCaptionVM: MagazineCellDataType {

    var sizeMode: MagazineLayoutItemSizeMode {
        return MagazineLayoutItemSizeMode(widthMode: .fullWidth(respectsHorizontalInsets: true),
                                          heightMode: .dynamic)
    }

    let title: String

    init(title: String) {
        self.title = title
    }

    var diffHash: Int {
        return title.hashValue
    }

    func configurator() -> CellConfigurator {
        return RowCaptionConfigurator(item: self)
    }

    func didSelect() {
        //
    }
}

